
'use client';

import { useEffect, useRef, useState } from 'react';

const backgroundMusicDataUri = "data:audio/mpeg;base64,-";

export function BackgroundMusic() {
  const audioRef = useRef<HTMLAudioElement | null>(null);
  const [isMounted, setIsMounted] = useState(false);

  useEffect(() => {
    setIsMounted(true);
    // Correctly initialize the Audio object only on the client
    const audio_elem = new Audio(backgroundMusicDataUri);
    audio_elem.loop = true;
    audio_elem.volume = 0.1;
    audioRef.current = audio_elem;


    return () => {
      // Stop and clean up the audio element on unmount
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current = null;
      }
    };
  }, []);

  useEffect(() => {
    if (isMounted && audioRef.current) {
      const audio = audioRef.current;
      const playPromise = audio.play();

      if (playPromise !== undefined) {
        playPromise.catch((error) => {
          console.warn("Background music autoplay was prevented:", error);
          // A common strategy to handle autoplay restriction is to play on the first user interaction.
          const playOnFirstClick = () => {
            audio.play().catch((e) => console.error("Could not play audio on click", e));
            window.removeEventListener('click', playOnFirstClick);
          };
          window.addEventListener('click', playOnFirstClick);

          // Cleanup the event listener when the component unmounts
          return () => {
            window.removeEventListener('click', playOnFirstClick);
          };
        });
      }
    }
  }, [isMounted]);

  // We don't render an <audio> tag directly, as we are controlling playback via the Audio constructor.
  return null;
}
